import React, { useState } from 'react';
import ReturnRenting from './ReturnRenting'; // Import your ReturnRenting component

const ParentComponent = () => {
    const [selectedRentings, setSelectedRentings] = useState([]);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    // Method to clear selected rentals
    const clearSelectedRentings = () => {
        setSelectedRentings([]); // Clears the selectedRentings array
    };

    return (
        <div>
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

            {/* Pass the required props to ReturnRenting */}
            <ReturnRenting
                selectedRentings={selectedRentings}
                setSuccessMessage={setSuccessMessage}
                setErrorMessage={setErrorMessage}
                clearSelectedRentings={clearSelectedRentings}
            />
        </div>
    );
};

export default ParentComponent;
