import React, { useState } from "react";
import { Button, Col, Container, Row, Alert } from "react-bootstrap";
import classes from "./Zwrot.module.css";
import connection from "../../axios";

function ReturnRenting() {
    const [rentalId, setRentalId] = useState("");
    const [confirmationMessage, setConfirmationMessage] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    const submit = () => {
        if (!rentalId) {
            setErrorMessage("Proszę podać numer wypożyczenia.");
            setConfirmationMessage("");
            return;
        }

        // Send the POST request for returning rental by rental ID
        connection.post('/api/rentings/return/{idRenting}', null, { params: { idRenting: rentalId } })
            .then((response) => {
                console.log(response);
                setConfirmationMessage("Wypożyczenie zostało pomyślnie zwrócone!");
                setErrorMessage("");
                setRentalId(""); // Clear the input after success
            })
            .catch((error) => {
                console.log(error);
                setErrorMessage("Wystąpił błąd podczas zwrotu wypożyczenia.");
                setConfirmationMessage("");
            });
    };

    return (
        <div>
            <Container className={classes.Form}>
                <Row>
                    <Col>
                        <label>Nr zdawanego wypożyczenia:</label>
                        <input
                            type="number"
                            id="input-nrWypozyczenia"
                            value={rentalId}
                            onChange={(e) => setRentalId(e.target.value)}
                            placeholder="Nr wypożyczenia"
                            className={classes.Input}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col className={classes.Button}>
                        <Button variant="primary" onClick={submit}>
                            Zatwierdź zwrot
                        </Button>
                    </Col>
                </Row>
                {confirmationMessage && (
                    <Row>
                        <Col>
                            <Alert variant="success" className={classes.Alert}>
                                {confirmationMessage}
                            </Alert>
                        </Col>
                    </Row>
                )}
                {errorMessage && (
                    <Row>
                        <Col>
                            <Alert variant="danger" className={classes.Alert}>
                                {errorMessage}
                            </Alert>
                        </Col>
                    </Row>
                )}
            </Container>
        </div>
    );
}

export default ReturnRenting;
