import {useState} from "react";
import React from "react";
import { Button, Alert } from "react-bootstrap";
import connection from "../../axios";

const ReturnRenting = ({ selectedRentings }) => {
    const [successMessage, setSuccessMessage] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    const submitReturns = () => {
        setSuccessMessage("");
        setErrorMessage("");

        selectedRentings.forEach(idRenting => {
            // Assuming you don't need to send any additional information for the update
            const updateRenting = {};

            connection.put(`/api/rentings/return/${idRenting}`, updateRenting)
                .then((response) => {
                    setSuccessMessage(`Rental with ID ${idRenting} returned successfully.`);
                })
                .catch((error) => {
                    setErrorMessage(`Error returning rental with ID ${idRenting}: ${error.message}`);
                });
        });

        // Clear selection after submission
        setSelectedRentings([]);
    };

    return (
        <div>
            <Button variant="light" onClick={submitReturns} disabled={selectedRentings.length === 0}>
                Zatwierd≈∫ zwroty
            </Button>
            {successMessage && <Alert variant="success" className="mt-3">{successMessage}</Alert>}
            {errorMessage && <Alert variant="danger" className="mt-3">{errorMessage}</Alert>}
        </div>
    );
};

export default ReturnRenting;
