import React, { useState } from 'react';
import {useNavigate} from 'react-router-dom';
import connection from "../../axios";

const Login = () => {
    const [appUserName, setAppUserName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const [error, setError] = useState('');

    // Funkcja do kodowania danych logowania w Base64
    const encodeBase64 = (username, password) => {
        return btoa(`${username}:${password}`);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const basicAuthHeader = `Basic ${encodeBase64(appUserName, password)}`;

        connection.get("http://localhost:8080/api/appuser/login", {
            headers: {
                'Authorization': basicAuthHeader
            }
        })
            .then(response => {
                // Obsługa poprawnej odpowiedzi
                console.log("Zalogowano pomyślnie:", response.data);
                navigate("/");  // Przekierowanie na stronę główną po udanym logowaniu
            })
            .catch(err => {
                console.error("Błąd logowania:", err);
                if (err.response && err.response.status === 401) {
                    setError("Błędna nazwa użytkownika lub hasło.");
                } else {
                    setError("Wystąpił błąd. Spróbuj ponownie.");
                }
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={appUserName}
                onChange={(e) => setAppUserName(e.target.value)}
                placeholder="Nazwa użytkownika"
                required
            />
            <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Hasło"
                required
            />
            <button type="submit">Zaloguj się</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
};

export default Login;
