import React, { useState } from "react";
import classes from "./RentalAgreement.module.css";
import { Button, Form, Alert } from "react-bootstrap";
import connection from "../../axios";
import DisplayRentalAgreement from "./DisplayRentalAgreement"; // Import the display component

function RentalAgreement() {
    const [count, setCount] = useState(0);
    const [rentingShow, setRentingShow] = useState(null); // To store renting data
    const [errorMessage, setErrorMessage] = useState(""); // To store error messages

    const sendForm = () => {
        connection.get("/api/rentings/show", {params: {idRenting: count}})
            .then((response) => {
                console.log("OK! ", response.data);
                setRentingShow(response.data); // Pass the data to the display component
                setErrorMessage(""); // Clear previous error messages
            })
            .catch((errorResponse) => {
                console.log("Error: ", errorResponse);
                setRentingShow(null); // Clear display if error
                setErrorMessage("Failed to retrieve data. Please try again."); // Set error message
            });
    };

    const changeCount = (event) => {
        const value = event.target.value;
        console.log(value);
        setCount(value);
    };

    return (
        <div>
            <Form>
                <Form.Group className={classes.Label}>
                    <Form.Label>Podaj nr Umowy</Form.Label>
                    <Form.Control type={'number'} onChange={changeCount} />
                    <Form.Text className={'text-bg-dark'}>
                        Formularz przekieruję Cię do Umowy wypożyczenia
                    </Form.Text>
                </Form.Group>
                <Button className={classes.FormButtonSubmit} variant={'dark'} onClick={sendForm}>
                    Wyślij
                </Button>
            </Form>
            {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
            {/* Render the DisplayAgreement component and pass the data */}
            {rentingShow && <DisplayRentalAgreement renting={rentingShow} />}
        </div>
    );
}

export default RentalAgreement;
