import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import connection from "../../axios";

const CreateUser = () => {
    const [appUserName, setAppUserName] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        const appUser = {
            appUserName,
            password,
        };

        try {
            const response = await connection.post('/api/appusers/devel/register', appUser);
            setSuccessMessage(response.data); // Zapisz komunikat o sukcesie
            setError(''); // Wyczyść ewentualny błąd
            // Przekierowanie lub inne działania po rejestracji
            navigate('/login'); // Przykładowe przekierowanie na stronę logowania
        } catch (err) {
            console.error("Błąd rejestracji:", err);
            if (err.response) {
                setError(err.response.data || 'Wystąpił błąd podczas rejestracji.');
            } else {
                setError('Wystąpił błąd. Spróbuj ponownie.');
            }
        }
    };

    return (
        <div>
            <h2>Rejestracja Użytkownika</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nazwa użytkownika:</label>
                    <input
                        type="text"
                        value={appUserName}
                        onChange={(e) => setAppUserName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Hasło:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Zarejestruj się</button>
            </form>
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default CreateUser;
