import {useState} from "react";
import React from "react";
import { Button, Alert } from "react-bootstrap";
import connection from "../../axios";

const ReturnRenting = ({ selectedRentings }) => {
    const [successMessage, setSuccessMessage] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    const submitReturns = () => {
        setSuccessMessage("");
        setErrorMessage("");

        selectedRentings.forEach(idRenting => {
            connection.post('/api/rentings/return/{idRenting}', null, { params: { idRenting: idRenting } })
                .then((response) => {
                    console.log(`Rental ${idRenting} returned successfully`, response);
                    setSuccessMessage(`Zwrot ${idRenting} wykonano pomyslnie.`);
                })
                .catch((error) => {
                    console.error(`Error returning rental ${idRenting}`, error);
                    setErrorMessage(`Błąd zwrotu wypożyczenia ${idRenting}: ${error.message}`);

                });
        });
    };

    return (
        <div>
            <Button variant="light" onClick={submitReturns} disabled={selectedRentings.length === 0}>
                Zatwierdź zwroty
            </Button>
            {successMessage && <Alert variant="success" className="mt-3">{successMessage}</Alert>}
            {errorMessage && <Alert variant="danger" className="mt-3">{errorMessage}</Alert>}
        </div>
    );
};

export default ReturnRenting;
